import axios from 'axios';

const USER_API_BASE_URL = 'http://localhost:3000/users';

class UserService {
     getUsers() {
        try {
            const response =  axios.get(USER_API_BASE_URL);
            return response.data;
        } catch (error) {
            console.log(error);
            throw error;
        }
    }
     createUser(user) {
        try {
            const response =  axios.post(USER_API_BASE_URL, user);
            return response.data;
        } catch (error) {
            console.log(error);
            throw error;
        }
    }
    getUserById(userId) {
        try {
            const response = axios.get(USER_API_BASE_URL + '/' + userId);
            return response.data;
        }
        catch (error) {
            console.log(error);
            throw error;
        }
    }
    updateUser(user, userId) {
        try {
            const response = axios.put(USER_API_BASE_URL + '/' + userId, user);
                return response.data;
        }
        catch (error) {
            console.log(error);
            throw error;
        }
    }
    deleteUser(userId) {
        try {
            const response = axios.delete(USER_API_BASE_URL + '/' + userId);
            return response.data;
        } catch (error) {
            console.log(error);
            throw error;
        }
    }
}

export default new UserService();
